function Navbar() {
  const [dropdown, setDropdown] = useState(false);
  const [buttons, setButtons] = useState([
    { label: "Sign Up", value: false },
    { label: "Add to Cart", value: false }
  ]);

  const handleButtonsChange = ({
    buttons,
    setButtons,
    handleButtonsChange
  }) => label => {
    const newButtonsState = buttons.map(button => {
      if (button.label === label) {
        return (button = { label: button.label, value: true });
      }
      return {
        label: button.label,
        value: false
      };
    });
    setButtons(newButtonsState);
  };
  return (
    <>
      <nav className="navbar">
      <div class='navbar-logo'>
            <Link to='/' className='menu-icon'>
              A and A
              <i class='fab fa-typo3' />
            </Link>
          </div>
        <ul className="nav-items">
          {navItems.map((item) => {
            if (item.title === "Products") {
              return (
                <li
                  key={item.id}
                  className={item.cName}
                  onMouseEnter={() => setDropdown(true)}
                  onMouseLeave={() => setDropdown(false)}
                >
                  <Link to={item.path}>{item.title}</Link>
                  {dropdown && <Dropdown />}
                </li>
              );
            }
            return (
              <li key={item.id} className={item.cName}>
                <Link to={item.path}>{item.title}</Link>
              </li>
            );
          })}
        </ul>
        <Specialbuton {...{ buttons, setButtons, handleButtonsChange }} />
      {buttons[0].value && <div>Sign Up</div>}
      {buttons[1].value && <div>Add to Cart</div>}
      
        {/* <div className='hero-btns'>
        <Button_SU
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'>
          Sign Up
        </Button_SU>
      </div> */}
        {/* <Button /> */}
      </nav>
    </>
  );
}

export default Navbar;
